using Mailtrap.SDK.Clients;
using Mailtrap.SDK.Extensions;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Hosting;

var builder = Host.CreateApplicationBuilder(args);

builder.Services.AddMailtrapClient(new MailtrapClientOptions
{
    ApiToken = "3c4b504083d9f33aacf7a522dc4f4573",
    BaseUrl = "https://send.api.mailtrap.io/"
});

using IHost host = builder.Build();

var client = host.Services.GetService<IMailtrapClient>();

var request = new SendEmailRequest()
{
    Recipients = new[] { new EmailAddress("/*Put your email here*/") },
    Sender = new EmailAddress("mailtrap@demomailtrap.com", "Mailtrap Test"),
    Subject = "You are awesome!",
    Html = "<p>Congratulations on your order no. <strong>1234</strong>." + DateTime.Now + "</p>",
    Attachments = new []
    {
        new Attachment()
        {
            Content = "",
            Filename = "mailtrap-new-logo.jpg", 
            Disposition = "attachment",
            Type =  "jpg"
        }
    }
};

var result = await client.SendAsync(request);

Console.WriteLine(result);

Console.ReadLine();
